$(".fancybox").fancybox({padding:0,fitToView:!0,maxWidth:"100%",modal:!1,helpers:{buttons:{},overlay:{opacity:.5,css:{"background-color":"#111"}}},afterLoad:function(){data=this.element.data(),name=data.imageable;var a=this.element.attr("href"),e="<div class='links hidden'>                       <img src="+a+" class='overlay-image' /><b>"+name+"</b></div>";$(".fancybox-overlay").append(e)}});