$(document).ready(function(){Dropzone.autoDiscover=!1,$("#new_upload").dropzone({paramName:"upload[image]",addRemoveLinks:!0,success:function(e,o){$(e.previewTemplate).find(".dz-remove").attr("id",o.fileID),$(e.previewElement).addClass("dz-success")},removedfile:function(e){var o=$(e.previewTemplate).find(".dz-remove").attr("id");$.ajax({type:"DELETE",url:"/uploads/"+o,success:function(e){console.log(e.message)}})}})});